create table DEPOSITS
(
    DEPOSIT_ID          NUMBER not null
        primary key,
    TITLE               VARCHAR2(255),
    AMOUNT              FLOAT,
    CURRENCY_ID         NUMBER
        constraint FK_DEPOSITS_CURRENCIES
            references CURRENCIES,
    INTEREST_PERCENTAGE NUMBER,
    DESCRIPTION         CLOB,
    USER_ID             NUMBER
        constraint FK_DEPOSITS_USERS
            references USERS,
    CREATE_DATE         DATE,
    DEADLINE            DATE,
    STATUS              FLOAT
)
/

